<template>
  <div>
    <x-header>
      <span>注册</span>
    </x-header>
    <div class="center">
      <svg version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" width="243px" height="79px" viewBox="0 0 243 79" enable-background="new 0 0 243 79" xml:space="preserve">  <image id="image0" width="243" height="79" x="0" y="0"    xlink:href="data:image/png;base64," /></svg>
      <tab custom-bar-width="60px" bar-active-color="red" active-color="black">
        <tab-item selected >用户注册</tab-item>
      </tab>
      <group>
        <x-input title="账号" name="username" v-model="user.username" placeholder="请输入账号" ref="username" required label-width="80px"></x-input>
        <x-input title="密码" name="password" v-model="user.password" placeholder="请输入密码" ref="password" type="password" :min="6" required label-width="80px"></x-input>
        <x-input title="邮箱" name="email" v-model="user.email" placeholder="请输入邮箱地址" is-type="email" label-width="80px"></x-input>
        <x-input title="手机号码" name="mobile" v-model="user.mobile" placeholder="请输入手机号码" keyboard="number" is-type="china-mobile" label-width="80px"></x-input>
      </group>
      <box gap="10px 10px">
        <x-button text="注册" type="warn" @click.native="register">注册</x-button>
        <x-button text="登录" link="/login">登录</x-button>
      </box>
    </div>
    
  </div>
</template>

<script>
import {AlertModule, Box, Group, Cell,Tab, TabItem, XButton,XInput } from "vux";
export default {
  components: {
    Box,
    Cell,
    Group,
    Tab,
    TabItem,
    XButton,
    XInput
  },
  methods:{
    register () {
      var self = this
      if(self.$refs.username.valid && self.$refs.password.valid){
        self.$http.post("/api/register", self.user).then(({data}) => {
          if(data.code == 1){
             AlertModule.show({
              title: '成功',
              content: data.msg,
              onHide () {
                self.$router.push('/login');
              }
            })
          }else if(data.code == 0){
            AlertModule.show({
              title: '失败',
              content: data.msg,
              onShow () {
                self.user = {
                  username:'',
                  password:''
                }
              }
            })
          }
        })
      }
    }
  },
  data(){
    return{
      user:{
        username:'',
        password:'',
        mobile:'',
        email:''
      }
    }
  }
};
</script>

<style>
.center {
  margin-top: 15px;
  text-align: center;
}
</style>
